export function getMargins(t){const e="string"==typeof t?document.querySelector(t):t;if(!e)throw new Error("Element not found");const r=window.getComputedStyle(e);return{top:parsePixelValue(r.marginTop),right:parsePixelValue(r.marginRight),bottom:parsePixelValue(r.marginBottom),left:parsePixelValue(r.marginLeft),all:r.margin}}export function getMargin(t,e){return getMargins(t)[e]}export function parsePixelValue(t){return parseInt(t,10)||0}export function hasMargin(t){const e=getMargins(t);return Object.values(e).some((t=>"number"==typeof t&&t>0))}export function getHorizontalMargin(t){const e=getMargins(t);return e.left+e.right}export function getVerticalMargin(t){const e=getMargins(t);return e.top+e.bottom}